/* eslint-disable jsx-a11y/img-redundant-alt */
import React, { useState } from 'react';
import './Landing.css';

const Landing = () => {
  const ImageComponent = () => {
    return (
      <img
        className="landing-image"
        alt="landing page photo"
        src="/landing/launchPhoto.png"
      />
    );
  };

  const [showContent1, setShowContent1] = useState(false);
  const [showContent2, setShowContent2] = useState(false);
  const [showContent3, setShowContent3] = useState(false);
  const [showContent4, setShowContent4] = useState(false);

  return (
    <div className="landing-container">
      <div className="landing-header">
        <div className="header-content">
          <div className="header-title">
            Earn Passive and Active Income through Crowdfunded Real Estate Investments
          </div>
          <div className="header-subtitle">
            Experience Hassle-Free Real Estate Investing at your Fingertips. Manage your portfolio and join related communities.
          </div>
          <button
            className="learn-more-button"
            onClick={() => {
              const scrollTarget = document.getElementById('scrollTarget');
              if (scrollTarget) {
                scrollTarget.scrollIntoView({ behavior: 'smooth' });
              }
            }}
          >
            <div className="learn-more-text">
              Learn More
            </div>
          </button>
        </div>
        <div className="header-image">
          <ImageComponent />
        </div>
      </div>

      <div id="scrollTarget" className="scroll-target">
        <div className="section-title">What Sets Us Apart</div>
        <div className="content-section">
          <div
            className="content-item"
            onMouseEnter={() => setShowContent1(true)}
            onMouseLeave={() => setShowContent1(false)}
          >
            <div className="content-title">Diverse Portfolio</div>
            {showContent1 && (
              <div className="content-details">
                Drive into RealEstateShowtime vast investment landscape and ecosystem.
              </div>
            )}
          </div>
          <div
            className="content-item"
            onMouseEnter={() => setShowContent2(true)}
            onMouseLeave={() => setShowContent2(false)}
          >
            <div className="content-title">Flexible Investment</div>
            {showContent2 && (
              <div className="content-details">
                Sophtera empowers you to dictate your investment, be it active or passive investing.
              </div>
            )}
          </div>
          <div
            className="content-item"
            onMouseEnter={() => setShowContent3(true)}
            onMouseLeave={() => setShowContent3(false)}
          >
            <div className="content-title">AI Driven Decisions</div>
            {showContent3 && (
              <div className="content-details">
                Experience the cutting-edge advantage of AI-driven decisions with Sophtera.
              </div>
            )}
          </div>
          <div
            className="content-item"
            onMouseEnter={() => setShowContent4(true)}
            onMouseLeave={() => setShowContent4(false)}
          >
            <div className="content-title">Worldwide Access</div>
            {showContent4 && (
              <div className="content-details">
                Embark on a global investment journey with Sophtera.
              </div>
            )}
          </div>
        </div>
      </div>

      <footer className="landing-footer">
        <div>Copyright 2024 - 601 Spring 2024</div>
        <div>Investment Made Easy!</div>
      </footer>
    </div>
  );
};

export default Landing;
