import React, { useState } from 'react';
import './Homepage.css';

const Landing = () => {
  const ImageComponent = () => {
    return (
      <img
        className="image"
        alt="landing page photo"
        src="/landing/launchPhoto.png"
      />
    );
  };

  const [showContent1, setShowContent1] = useState(false);
  const [showContent2, setShowContent2] = useState(false);
  const [showContent3, setShowContent3] = useState(false);
  const [showContent4, setShowContent4] = useState(false);

  return (
    <div className="container">
      <div className="header">
        <div className="header-content">
          <div>
            <h2 className="header-title">
              Earn Passive and Active Income through Crowdfunded Real Estate Investments
            </h2>
            <p className="header-subtitle">
              Experience Hassle-Free Real Estate Investing at your Fingertips.
            </p>
          </div>
          <button
            className="learn-more-button"
            onClick={() => {
              const scrollTarget = document.getElementById('scrollTarget');
              if (scrollTarget) {
                scrollTarget.scrollIntoView({ behavior: 'smooth' });
              }
            }}
          >
            Learn More
          </button>
        </div>
        <div className="image-container">
          <ImageComponent />
        </div>
      </div>

      <div id="scrollTarget" className="section">
        <h1>What Sets Us Apart</h1>
        <div className="card-container">
          <div
            className="card"
            onMouseEnter={() => setShowContent1(true)}
            onMouseLeave={() => setShowContent1(false)}
          >
            <h3 className="card-title">Diverse Portfolio</h3>
            {showContent1 && (
              <div className="card-content">
                Drive into RealEstateShowtime vast investment landscape and ecosystem.
              </div>
            )}
          </div>

          <div
            className="card"
            onMouseEnter={() => setShowContent2(true)}
            onMouseLeave={() => setShowContent2(false)}
          >
            <h3 className="card-title">Flexible Investment</h3>
            {showContent2 && (
              <div className="card-content">
                Sophtera empowers you to dictate your investment, be it active or passive investing.
              </div>
            )}
          </div>

          <div
            className="card"
            onMouseEnter={() => setShowContent3(true)}
            onMouseLeave={() => setShowContent3(false)}
          >
            <h3 className="card-title">AI Driven Decisions</h3>
            {showContent3 && (
              <div className="card-content">
                Experience the cutting edge advantage of AI-driven decisions with Sophtera.
              </div>
            )}
          </div>

          <div
            className="card"
            onMouseEnter={() => setShowContent4(true)}
            onMouseLeave={() => setShowContent4(false)}
          >
            <h3 className="card-title">Worldwide Access</h3>
            {showContent4 && (
              <div className="card-content">
                Embark on a global investment journey with Sophtera.
              </div>
            )}
          </div>
        </div>
      </div>

      <footer className="footer">
        <span>Copyright 2024 - 601 Spring 2024</span>
        <span>Investment Made Easy!</span>
      </footer>
    </div>
  );
};

export default Landing;
