import React, { useState } from 'react';
import './Landing.css';

const Landing = () => {
  const ImageComponent = () => {
    return (
      <img
        className="image-component"
        alt="landing page photo"
        src="/landing/launchPhoto.png"
      />
    );
  };
  const [showContent1, setShowContent1] = useState(false);

  return (
    <div className="landing-container">
      <div className="header-section">
        <div className="header-content">
          <div className="header-title">
            Earn Passive and Active Income through Crowdfunded Real Estate Investments
          </div>
          <div className="header-description">
            Experience Hassle-Free Real Estate Investing at your Fingertips. Manage your portfolio and join related communities.
          </div>
          <button
            className="scroll-button"
            onClick={() => {
              const scrollTarget = document.getElementById('scrollTarget');
              if (scrollTarget) {
                scrollTarget.scrollIntoView({ behavior: 'smooth' });
              }
            }}
          >
            Learn More
          </button>
        </div>
        <ImageComponent />
      </div>

      <div id="scrollTarget" className="card-container">
        <div
          className="card"
          onMouseEnter={() => setShowContent1(true)}
          onMouseLeave={() => setShowContent1(false)}
        >
          <div className="card-title">Diverse Portfolio</div>
          {showContent1 && (
            <div className="hidden-content">
              Dive into a vast investment landscape and ecosystem.
            </div>
          )}
        </div>
        {/* Repeat for other cards */}
      </div>

      <footer className="footer">
        <div className="footer-text">Copyright 2024 - 601 Spring 2024</div>
        <div className="footer-text">Investment Made Easy!</div>
      </footer>
    </div>
  );
};

export default Landing;
