import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';
import logo from '../../assets/images/logo.png';
import LoginButton from './LoginButton';
import LogoutButton from './LogoutButton';

// Dropdown component for handling menus like "Who We Are", "Solutions", "Insight"
const Dropdown = ({ title, items }) => (
  <div className="relative group inline-block">
    <button className="font-medium text-neutralgray-600">{title}</button>
    <div className="hidden group-hover:block absolute bg-white mt-2 py-2 w-80 z-100">
      {items.map((item, index) => (
        <Link
          key={index}
          to={`/${item.replace(/\s+/g, '-').toLowerCase()}`}
          className="block py-2 text-gray-800 hover:bg-gray-100"
        >
          {item}
        </Link>
      ))}
    </div>
  </div>
);

const Navbar = () => {
  const { user, isLoading } = useAuth0();

  // Define the menu items for the dropdowns
  const whoWeAreItems = ['Our Story', 'Our Team', 'Careers'];
  const solutionsItems = ['Investment Management', 'Corporate Banking', 'Debt Finance Consulting'];
  const insightItems = ['News', 'Event'];

  return (
    <div className="bg-white border-b-[1px] border-solid border-gainsboro-100 py-4">
      <div className="container mx-auto flex justify-around items-center px-4 md:px-8">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-primaryblue-500">
        <img src={logo} alt="logo" className="w-[150px] h-auto" />
        </Link>


        <div className="flex space-x-8">
          {/* Dropdowns */}
          <Dropdown title="Who We Are" items={whoWeAreItems} />
          <Dropdown title="Solutions" items={solutionsItems} />
          <Dropdown title="Insight" items={insightItems} />

          {/* Conditional rendering for logged in/out status */}
          {user ? (
            <>
              <Link to="/investment-products" className="font-medium text-neutralgray-600 hover:text-primaryblue-500">
                Investment Products
              </Link>
              <LogoutButton />
            </>
          ) : (
            <>
              <LoginButton />
            </>
          )}
        </div>

        {/* User Information */}
        <div className="flex items-center">
          {!isLoading && user && (
            <span className="font-medium text-neutralgray-600">{user.name}</span>
          )}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
